public class SimpleEntity {

//    @Id
//    @GeneratedValue(strategy = GenerationType.IDENTITY)
    public int id;

//    @Column(name = "en")
    public String name;

    public SimpleEntity(int id, String name) {
        super();
        this.id = id;
        this.name = name;
    }

    public SimpleEntity() {

    }

    public int getId() {
        return this.id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public String getName() {
        return this.name;
    }

    public void setName(String name) {
        this.name = name;
    }

}
